function Player (socket, game) {
  this.socket = socket;
  this.game = game;
  this.ready = false;
  this.offset = {
    top: 0,
    left: 0
  };

  this.events();
}

Player.prototype.events = function () {
  var that = this;
  this.socket.on('ready', function () {
    that.ready = true;
    if(that.game.isAllPlayersReady()) {
      that.game.broadcast('start');
    }
  });
}

module.exports = Player;
